import React from 'react';
import Layout from '../components/Layout';
import { Link } from 'react-router-dom';

const ForHim = () => {
  const menProducts = [
    {
      id: 2,
      name: "Blu",
      description: "A warm, woody fragrance with hints of vanilla and musk.",
      price: 7999,
      images: ["/images/product2.jpg"],
      category: "for-him"
    },
    {
      id: 3,
      name: "Oud Intense",
      description: "A rich, intense oud fragrance with deep woody and spicy notes.",
      price: 8999,
      images: ["/images/product3.jpg"],
      category: "for-him"
    }
  ];

  const formatIndianRupees = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(amount);
  };

  return (
    <Layout>
      <section className="py-16 bg-gray-900 min-h-screen">
        <div className="container mx-auto px-4">
          <h1 className="text-4xl md:text-5xl font-bold text-gold-500 text-center mb-4">For Him</h1>
          <p className="text-gray-300 text-lg text-center mb-12 max-w-2xl mx-auto">
            Discover our collection of sophisticated fragrances crafted for the modern man.
          </p>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8 max-w-4xl mx-auto">
            {menProducts.map((product) => (
              <div key={product.id} className="bg-gray-800 shadow-2xl rounded-xl p-6 border border-gray-700 hover:border-gold-500 transition-all duration-300">
                <img
                  src={product.images[0]}
                  alt={product.name}
                  className="rounded-lg w-full h-80 object-cover mb-4"
                />
                <h3 className="text-2xl font-semibold text-gold-400 mb-2">{product.name}</h3>
                <p className="text-gray-400 text-sm mb-4 font-sans leading-relaxed">
                  {product.description}
                </p>
                <p className="text-gold-500 font-bold text-xl mb-4 font-sans">
                  {formatIndianRupees(product.price)}
                </p>
                <div className="flex justify-between space-x-3">
                  <Link 
                    to={`/product/${product.id}`}
                    className="flex-1 px-4 py-2 bg-gray-700 text-gold-300 text-sm font-medium rounded hover:bg-gray-600 transition font-sans text-center"
                  >
                    View Details
                  </Link>
                  <button className="flex-1 px-4 py-2 bg-gold-600 text-black text-sm font-medium rounded hover:bg-gold-500 transition font-sans">
                    Add to Cart
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>
    </Layout>
  );
};

export default ForHim;
