import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import Layout from '../components/Layout';

const ProductDetail = () => {
  const { id } = useParams();
  const [selectedImage, setSelectedImage] = useState(0);
  const [quantity, setQuantity] = useState(1);

  // Product data for all 5 products
  const products = {
    1: {
      id: 1,
      name: "Mystic",
      description: "A fresh, modern fragrance with notes of citrus and amber that evolves throughout the day.",
      longDescription: "Mystic is a captivating unisex fragrance that opens with bright citrus notes of bergamot and mandarin, revealing a heart of delicate amber and warm musk. Perfect for daytime wear, it leaves a subtle, memorable trail that works beautifully for both men and women.",
      price: 7499,
      images: [
        "/images/product1.jpg",
        "/images/product1-alt1.jpg", 
        "/images/product1-alt2.jpg"
      ],
      features: [
        "Long-lasting 8+ hours",
        "Natural essential oils",
        "Alcohol-free formula",
        "Cruelty-free",
        "Unisex fragrance"
      ],
      notes: {
        top: ["Bergamot", "Mandarin", "Lemon Zest"],
        middle: ["Amber", "White Flowers", "Lily"],
        base: ["Musk", "Sandalwood", "Vanilla"]
      },
      category: "unisex"
    },
    2: {
      id: 2,
      name: "Blu",
      description: "A warm, woody fragrance with hints of vanilla and musk.",
      longDescription: "Blu is a sophisticated masculine fragrance that combines warm woody notes with subtle hints of vanilla and musk. This versatile scent transitions seamlessly from day to evening, making it perfect for the modern man who appreciates timeless elegance.",
      price: 7999,
      images: [
        "/images/product2.jpg",
        "/images/product2-alt1.jpg",
        "/images/product2-alt2.jpg"
      ],
      features: [
        "Long-lasting 10+ hours",
        "Woody aromatic profile",
        "Natural ingredients",
        "Cruelty-free"
      ],
      notes: {
        top: ["Bergamot", "Cardamom", "Lavender"],
        middle: ["Cedarwood", "Patchouli", "Vetiver"],
        base: ["Vanilla", "Musk", "Amberwood"]
      },
      category: "for-him"
    },
    3: {
      id: 3,
      name: "Oud Intense",
      description: "A rich, intense oud fragrance with deep woody and spicy notes.",
      longDescription: "Oud Intense is a powerful unisex fragrance that celebrates the rich heritage of oud. With deep woody notes complemented by exotic spices, this scent makes a bold statement while maintaining an air of sophistication suitable for both men and women.",
      price: 8999,
      images: [
        "/images/product3.jpg",
        "/images/product3-alt1.jpg",
        "/images/product3-alt2.jpg"
      ],
      features: [
        "Long-lasting 12+ hours",
        "Premium oud composition",
        "Concentrated formula",
        "Cruelty-free",
        "Unisex fragrance"
      ],
      notes: {
        top: ["Saffron", "Bergamot", "Pink Pepper"],
        middle: ["Oud", "Rose", "Agarwood"],
        base: ["Sandalwood", "Amber", "Leather"]
      },
      category: "unisex"
    },
    4: {
      id: 4,
      name: "Her",
      description: "An elegant floral fragrance with delicate notes of jasmine and rose.",
      longDescription: "Her is a beautifully feminine fragrance that celebrates the delicate beauty of flowers. With its elegant blend of jasmine, rose, and soft musk, this scent captures the essence of modern femininity - strong yet graceful, confident yet delicate.",
      price: 7299,
      images: [
        "/images/product4.jpg",
        "/images/product4-alt1.jpg",
        "/images/product4-alt2.jpg"
      ],
      features: [
        "Long-lasting 8+ hours",
        "Floral bouquet",
        "Natural extracts",
        "Cruelty-free"
      ],
      notes: {
        top: ["Pear", "Bergamot", "Green Notes"],
        middle: ["Jasmine", "Rose", "Lily of the Valley"],
        base: ["Musk", "Cedarwood", "Vanilla"]
      },
      category: "for-her"
    },
    5: {
      id: 5,
      name: "Luxury Perfume Gift Set - 4 x 20ml",
      description: "Experience our signature collection with this exquisite gift set featuring four distinct fragrances.",
      longDescription: "Our Luxury Perfume Gift Set is the perfect introduction to the world of SOUL fragrances. This exquisite set includes four of our most popular scents in elegant 20ml bottles, beautifully packaged in a premium gift box. Perfect for gifting or for those who love variety in their fragrance collection.",
      price: 4999,
      images: [
        "/images/product5.jpg",
        "/images/product5-alt1.jpg",
        "/images/product5-alt2.jpg"
      ],
      features: [
        "4 different fragrances",
        "20ml each bottle",
        "Premium gift packaging",
        "Perfect for travel",
        "Ideal for gifting"
      ],
      notes: {
        top: ["Mystic: Citrus & Amber", "Blu: Woody & Vanilla", "Oud Intense: Spicy & Woody", "Her: Floral & Musky"],
        middle: ["A complete fragrance experience"],
        base: ["Discover your signature scent"]
      },
      includes: ["Mystic 20ml", "Blu 20ml", "Oud Intense 20ml", "Her 20ml", "Elegant Gift Box"],
      category: "gift-sets"
    }
  };

  const product = products[id];

  // If product not found, show error
  if (!product) {
    return (
      <Layout>
        <section className="py-16 bg-gray-900 min-h-screen">
          <div className="container mx-auto px-4 text-center">
            <h1 className="text-3xl text-gold-500 mb-4">Product Not Found</h1>
            <Link to="/all-products" className="text-gold-300 hover:text-gold-500">
              Back to All Products
            </Link>
          </div>
        </section>
      </Layout>
    );
  }

  const addToCart = () => {
    console.log(`Added ${quantity} of ${product.name} to cart`);
    alert(`Added ${quantity} ${product.name} to cart!`);
  };

  const formatIndianRupees = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(amount);
  };

  return (
    <Layout>
      <section className="py-16 bg-gray-900 min-h-screen">
        <div className="container mx-auto px-4">
          <div className="max-w-6xl mx-auto">
            {/* Breadcrumb */}
            <nav className="flex mb-8 text-sm">
              <Link to="/" className="text-gold-500 hover:text-gold-300">Home</Link>
              <span className="mx-2 text-gray-500">/</span>
              <Link to="/all-products" className="text-gold-500 hover:text-gold-300">All Products</Link>
              <span className="mx-2 text-gray-500">/</span>
              <span className="text-gray-300">{product.name}</span>
            </nav>

            <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
              {/* Product Images */}
              <div>
                <div className="bg-gray-800 rounded-xl p-4 mb-4">
                  <img
                    src={product.images[selectedImage]}
                    alt={product.name}
                    className="w-full h-96 object-cover rounded-lg"
                  />
                </div>
                <div className="flex space-x-4">
                  {product.images.map((image, index) => (
                    <button
                      key={index}
                      onClick={() => setSelectedImage(index)}
                      className={`bg-gray-800 rounded-lg p-2 border-2 transition-all ${
                        selectedImage === index ? 'border-gold-500' : 'border-transparent hover:border-gold-300'
                      }`}
                    >
                      <img
                        src={image}
                        alt={`${product.name} view ${index + 1}`}
                        className="w-16 h-16 object-cover rounded"
                      />
                    </button>
                  ))}
                </div>
              </div>

              {/* Product Info */}
              <div>
                <h1 className="text-3xl md:text-4xl font-bold text-gold-500 mb-4">{product.name}</h1>
                <p className="text-2xl font-semibold text-gold-400 mb-6">{formatIndianRupees(product.price)}</p>
                
                <p className="text-gray-300 mb-6 leading-relaxed font-sans">{product.longDescription}</p>

                {/* Gift Set Includes */}
                {product.includes && (
                  <div className="mb-6">
                    <h3 className="text-xl font-semibold text-gold-400 mb-3">What's Included</h3>
                    <ul className="text-gray-300 space-y-2 font-sans">
                      {product.includes.map((item, index) => (
                        <li key={index} className="flex items-center">
                          <span className="text-gold-500 mr-2">•</span>
                          {item}
                        </li>
                      ))}
                    </ul>
                  </div>
                )}

                {/* Quantity Selector */}
                <div className="flex items-center mb-6">
                  <span className="text-gray-300 mr-4 font-sans">Quantity:</span>
                  <div className="flex items-center border border-gray-600 rounded">
                    <button
                      onClick={() => setQuantity(Math.max(1, quantity - 1))}
                      className="px-3 py-1 text-gold-500 hover:bg-gray-700 transition"
                    >
                      -
                    </button>
                    <span className="px-4 py-1 text-gray-300 font-sans">{quantity}</span>
                    <button
                      onClick={() => setQuantity(quantity + 1)}
                      className="px-3 py-1 text-gold-500 hover:bg-gray-700 transition"
                    >
                      +
                    </button>
                  </div>
                </div>

                {/* Add to Cart Button */}
                <button
                  onClick={addToCart}
                  className="w-full bg-gold-600 text-black font-semibold py-3 rounded-lg hover:bg-gold-500 transition mb-6 font-sans"
                >
                  Add to Cart - {formatIndianRupees(product.price * quantity)}
                </button>

                {/* Product Features */}
                <div className="mb-6">
                  <h3 className="text-xl font-semibold text-gold-400 mb-3">Features</h3>
                  <ul className="text-gray-300 space-y-2 font-sans">
                    {product.features.map((feature, index) => (
                      <li key={index} className="flex items-center">
                        <span className="text-gold-500 mr-2">•</span>
                        {feature}
                      </li>
                    ))}
                  </ul>
                </div>

                {/* Fragrance Notes */}
                <div>
                  <h3 className="text-xl font-semibold text-gold-400 mb-3">Fragrance Notes</h3>
                  <div className="grid grid-cols-3 gap-4 text-sm">
                    <div>
                      <h4 className="text-gold-500 font-semibold mb-2">Top Notes</h4>
                      <p className="text-gray-300 font-sans">{Array.isArray(product.notes.top) ? product.notes.top.join(', ') : product.notes.top}</p>
                    </div>
                    <div>
                      <h4 className="text-gold-500 font-semibold mb-2">Middle Notes</h4>
                      <p className="text-gray-300 font-sans">{Array.isArray(product.notes.middle) ? product.notes.middle.join(', ') : product.notes.middle}</p>
                    </div>
                    <div>
                      <h4 className="text-gold-500 font-semibold mb-2">Base Notes</h4>
                      <p className="text-gray-300 font-sans">{Array.isArray(product.notes.base) ? product.notes.base.join(', ') : product.notes.base}</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </Layout>
  );
};

export default ProductDetail;
